import { FETCH_CURRENT_USER } from '../actions/types';

const INTIAL_STATE = {
  isSignedIn: null,
  userId: null,
  accessToken: null
};

export default (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case FETCH_CURRENT_USER:
      return { ...state, accessToken: action.accessToken};
    default:
      return state;
  }
};
