import { SEARCH_BAND_VALUE_UPDATE, SEARCH_BANDS } from '../actions/types';

const INTIAL_STATE = {
  searchValue: null
};

const searchReducer = (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case SEARCH_BAND_VALUE_UPDATE:
      return { ...state, searchValue: action.payload};
    case SEARCH_BANDS:
      return { ...state, searchValue: action.payload};
    default:
      return state;
  }
};

export default searchReducer;
