import React from 'react';
import { connect } from 'react-redux';

import { SpotifyAuth, Scopes } from 'react-spotify-auth';

import SpotifyCallback from './SpotifyCallback';
import SearchSpotify from '../search/SearchSpotify';
import BandCard from '../BandCard';

class NewBandList extends React.Component {

  renderAuth() {
    if (!this.props.isSignedIn) {
      return (
        <div>
          <SpotifyAuth
            redirectUri='http://localhost:3000/bands/new'
            clientID='39df040e26444a399537c3a6ee349bd4'
            scopes={[Scopes.userReadPrivate, 'user-read-email']}
            title="Log in with Spotify"
            showDialog
          />
        </div>
      );
    }

    return (
      <div className="center">
        <SearchSpotify />
      </div>
    );
  }

  renderSearchResults = () => {
    const renderedSearchResults = this.props.searchResults.map(result => {
      return (
        <div key={result.bandId} className="row">
          <div className="column"><BandCard imagePath={result.profileImage} bandName={result.name} numStans={result.stanCount} /></div>
        </div>
      );
    });

    return renderedSearchResults;
  }

  renderBands() {

    if (!this.props.searchResults) {
      return <TopBands />;
    }

    return (
      <div>
        <div className="ui content" style={{ margin: '20px' }}>
          Don't see who you're looking for?
          <Link to="/bands/new" className="item">
            <span> Introduce a band to the site.</span>
          </Link>
        </div>
        <h2 className="ui header">Search Results</h2>
        <div className="ui stackable four column grid">
          {this.renderSearchResults()}
        </div>
      </div>
    );


  }

  render() {
    return (
      <div>
        <SpotifyCallback />
        {this.renderAuth()}
        <h2 className="ui header">Search Results</h2>
        <div className="ui stackable four column grid">
          {this.renderSearchResults()}
        </div>
      </div>
    );
  }

}

const mapStateToProps = state => {
  return {
    isSignedIn: state.auth.isSignedIn,
    searchValue: state.spotifySearch.value,
    searchResults: state.spotifySearch.results
  };
};

export default connect(
  mapStateToProps
)(NewBandList);
