import React from 'react';
import { connect } from 'react-redux';

import { stanBand, unstanBand } from '../../actions';
import Comment from './Comment';

class Band extends React.Component {

  onStanClick = () => {
    this.props.stanBand();
  }

  onUnstanClick = () => {
    this.props.unstanBand();
  }

  renderStanButton() {
    const currentUser = this.props.currentUser;
    const isSignedIn = this.props.isSignedIn;
    const isStanning = currentUser && currentUser.stanning.includes(this.props.currentBand.spotifyBandId);

    if (isSignedIn && isStanning) {
      return (
        <div onClick={this.onUnstanClick} className="ui secondary focusable button">
          <i className="check icon"></i>
          Unstan
        </div>
      );
    } else if (isSignedIn) {
      return (
        <div onClick={this.onStanClick} className="ui secondary focusable button">
          <i className="add icon"></i>
          Stan
        </div>
      );
    } else {
      return (
        <div className="ui secondary focusable button">
          <i className="add icon"></i>
          Log in to Stan
        </div>
      );
    }

  }

  renderComments() {

    const comments = this.props.currentBand.comments && this.props.currentBand.comments.length > 0 ? this.props.currentBand.comments : null;

    if (comments) {
      return (
        <div className="ui comments">
          {comments.map(comment => {
            return (
              <Comment key={comment.id} comment={comment} />
            );
          })}
          <form class="ui reply form">
            <div class="field">
              <textarea></textarea>
            </div>
            <div class="ui primary submit labeled icon button">
              <i class="icon edit"></i>
              Add Comment
            </div>
          </form>
        </div>
      );
    }

    return (
      <div>
        No comments
      </div>
    );

  }

  render() {
    return (
      <div style={{ marginTop: '35px' }}>

        <div className="ui segment">
          <h1 className="ui left floated header">{this.props.currentBand.name}</h1>
          <div className="ui right floated header">
            <div className="ui label" style={{ marginRight: '15px'}}>{this.props.currentBand.stans ? this.props.currentBand.stans.length : 0} stans</div>
            {this.renderStanButton()}
          </div>
          <div className="ui clearing divider"></div>
          <div className="ui items">
            <div className="item">
              <div className="center aligned">
                <img className="ui medium rounded image" src={this.props.currentBand.profileImage} alt={this.props.currentBand.name} />
                <div>
                  Introduced by {this.props.currentBand.introducerUserId}
                </div>
              </div>
              <div className="top aligned content">
              </div>
            </div>
          </div>
        </div>

        <div className="ui segment">
          {this.renderComments()}
        </div>


      </div>
    );
  }

}

const mapStateToProps = state => {
  return {
    currentBand: state.bandSearch.selected,
    isSignedIn: state.spotifyAuth.isSignedIn,
    currentUser: state.currentUser.user
  };
};

export default connect(
  mapStateToProps,
  { stanBand, unstanBand }
)(Band);
