import spotify from '../apis/spotify';
import bands from '../apis/bands';
import {
  ACCESS_TOKEN_RECEIVED,
  FETCH_CURRENT_USER,
  SIGN_OUT,
  FETCH_BANDS,
  FETCH_BAND,
  CREATE_BAND } from './types';

/* spotify action creators */

export const accessTokenReceived = token => async dispatch => {
  dispatch({ type: ACCESS_TOKEN_RECEIVED, payload: token });
};

export const fetchCurrentUser = () => async (dispatch, getState) => {
  const response = await spotify.get('/me', {
    headers: {
      Authorization: 'Bearer ' + getState().auth.accessToken
    }
  });

  dispatch({ type: FETCH_CURRENT_USER, payload: response.data.id });
};

export const signOut = () => async dispatch => {
  dispatch({ type: SIGN_OUT });
};

/* bands action creators */

export const fetchBands = () => async dispatch => {
  const response = await bands.get('/bands');

  dispatch({ type: FETCH_BANDS, payload: response.data });
}

export const fetchBand = id => async dispatch => {
  const response = await bands.get(`/bands/${id}`);

  dispatch({ type: FETCH_BAND, payload: response.data });
}

export const createBand = band => async (dispatch, getState) => {
  const { userId } = getState().auth;
  const response = await bands.post('/bands', { ...band, userId });

  dispatch({ type: CREATE_BAND, payload: response.data });
}

export const editBand = id => async dispatch => {
  const response = await bands.put(`/bands/${id}`, formValues);

  dispatch({ type: EDIT_BAND, payload: response.data });
};
