import { SEARCH_BAND_VALUE_UPDATE, SEARCH_BANDS, BAND_SELECTED } from '../actions/types';

const INTIAL_STATE = {
  value: null,
  results: null,
  selected: null
};

const bandSearchReducer = (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case SEARCH_BAND_VALUE_UPDATE:
      return { ...state, value: action.payload};
    case SEARCH_BANDS:
      return { ...state, results: action.payload};
    case BAND_SELECTED:
      return { ...state, selected: action.payload };
