import React from 'react';
import { connect } from 'react-redux';

import Search from '../Search';
import TopBands from '../TopBands';
import BandCard from '../BandCard';
import '../../styles/styles.css';

class Home extends React.Component {

  renderedSearchResults = () => {
    this.props.searchResults.map(result => {
      return (
        <div key={result.bandId} className="row">
          <div className="column"><BandCard imagePath={result.profileImage} bandName={result.name} numStans={result.stanCount} /></div>
        </div>
      );
    })
  }

  renderBands() {

    if (!this.props.searchResults) {
      return <TopBands />;
    }

    return (
      <div>
        <h2 className="ui header">Search Results</h2>
        <div className="ui stackable four column grid">

        {}

        </div>
      </div>
    );


  }

  render() {
    return (
      <div className="center">
        <Search />
        {this.renderBands()}
      </div>
    );
  }

}

const mapStateToProps = state => {
  return {
    searchValue: state.bandSearch.value,
    searchResults: state.bandSearch.results
  };
};

export default connect(
  mapStateToProps
)(Home);
