import { FETCH_USER_BY_ID } from '../actions/types';

const INTIAL_STATE = {
  user: null
};

const userReducer = (state = INTIAL_STATE, action) => {
  switch (action.type) {
    case FETCH_USER_BY_ID:
      return { ...state, user: action.payload};
    default:
      return state;
  }
};

export default userReducer;
