import spotify from '../apis/spotify';
import { ACCESS_TOKEN_RECEIVED, FETCH_CURRENT_USER } from './types';

export const accessTokenReceived = (token) => async dispatch => {
  console.log('access token received');
  dispatch({ type: ACCESS_TOKEN_RECEIVED, payload: response.data });
};

export const fetchCurrentUser = () => async dispatch => {
  console.log('fetching current user');
  const response = await spotify.get('/me');
  console.log(response);

  dispatch({ type: FETCH_CURRENT_USER, payload: response.data });
};
