import React from 'react';
import { connect } from 'react-redux';
import { searchBandValueUpdate } from '../actions';

class Search extends React.Component {

  constructor(props) {
    super(props);
    this.state = { searchValue: null };
  }

  _onKeyUp = (e) => {
    this.props.searchBandValueUpdate(e.target.value);

    if (e.key === 'Enter') {
      this.search();
    }
  }

  search = () => {
    console.log('search for band with name: ' + this.state.searchValue);
  }

  render() {
    return (
      <div style={{ margin: '50px' }}>
        <div className="ui fluid category search">
          <div className="ui icon input">
            <input onKeyUp={this._onKeyUp} className="prompt" type="text" placeholder="Search bands..." />
          </div>
          <button onClick={this.search} className="ui primary button" style={{ margin: '20px'}}>Go</button>
          <div className="results"></div>
        </div>
      </div>
    );
  }

}

const mapStateToProps = state => {
  return {
    searchValue: state.searchValue
  };
};

export default connect(
  mapStateToProps,
  { searchBandValueUpdate }
)(Search);
