import React from 'react';
import { connect } from 'react-redux';

class Band extends React.Component {

  render() {
    return (
      <div style={{ margin: '20px' }}>

        <h2 className="ui left floated header">{this.props.currentBand.name}</h2>
        <div className="ui clearing divider"></div>
        <img className="ui left floated medium rounded image" src={this.props.currentBand.profileImage} alt={this.props.currentBand.name} />

        <div className="ui right floated">
          <div className="ui horizontal label"><h3>{this.props.currentBand.stanCount} stans</h3></div>
          <button className="ui secondary focusable button"><h3>Stan</h3></button>
        </div>

        <div className="left band div">
          Band: {this.props.currentBand.name}<br/>
          Image: {this.props.currentBand.profileImage}<br/>
          Introduced By: {this.props.currentBand.introducerUserId}<br/>
          bandId: {this.props.currentBand.bandId}<br/>
        </div>
        <div className="right stan div">
          Stans: {this.props.currentBand.stanCount}<br/>
        </div>
      </div>
    );
  }

}

const mapStateToProps = state => {
  return {
    currentBand: state.bandSearch.selected
  };
};

export default connect(
  mapStateToProps
)(Band);
