import React from 'react';

class BandCard extends React.Component {

  render() {

    const imagePath = this.props.imagePath;
    const bandName = this.props.bandName;
    const numStans = this.props.numStans;

    return (
      <div>
        <div className="ui card">
          <label className="image">
            <img src={imagePath} alt={bandName} />
          </label>
          <div className="content">
            <label className="header">{bandName}</label>
            <div className="meta">
              {this.props.hideStansLabel ? null : <label>{numStans} Stans</label>}
            </div>
          </div>
          {
            ( this.props.hideStansLabel && this.props.isFollowing ) ?
              ( <div className="extra content">
                <a href="/#">
                  <i className="add icon"></i>
                  Add to Site
                </a>
              </div> ) :
            ( null )
          }
        </div>
      </div>
    );
  }

}

BandCard.defaultProps = {
  hideStansLabel: false
}

export default BandCard;
