import React from 'react';
import { connect } from 'react-redux';

class Stan extends React.Component {

  render() {
    return (
      <div style={{ marginTop: '35px' }}>

        <div className="ui segment">
          <h1 className="ui left floated header">Dummy Stan Name</h1>
          <div className="ui clearing divider"></div>
          <img className="ui centered medium rounded image" src="https://i.scdn.co/image/ab6775700000ee85326277237dbd9d4cd9ee1b04" alt={this.props.currentBand.name} />
          <div className="ui center aligned">
            <Link onClick={() => console.log('user clicked')} to="/stan">
              <div className="ui black label" style={{ margin: '12px' }}>
                Introduced by
                <div className="detail">
                  {this.props.currentBand.introducerUserId}
                </div>
              </div>
            </Link>
            <img className="ui centered mini circular image" src={this.state.imageUrl} alt="Introducer Profile"/>
          </div>
        </div>

        <div className="ui header" style={{ color: 'white' }}>Comment Thread</div>
        <div className="ui segment">
          <div className="ui form">
            {<div className="field">
              <label>Leave a comment</label>
              <textarea onChange={() => console.log('dummy value do nothing')} value={this.props.newComment ? this.props.newComment : ""} rows="2"></textarea>
            </div>}
            {<div onClick={this.onAddCommentClick} className="ui submit black button" style={{ marginBottom: '20px' }}>Add Comment</div>}
          </div>
          {/*this.renderComments()*/}
        </div>


      </div>
    );
  }

}

const mapStateToProps = state => {
  return {
    currentStan: state.stanSearch.selected
  };
};

export default connect(
  mapStateToProps
)(Stan);
