import React from 'react';
import { connect } from 'react-redux';

import Search from '../Search';
import TopBands from '../TopBands';
import BandCard from '../BandCard';
import '../../styles/styles.css';

class Home extends React.Component {

  renderBands() {

    if (!this.props.searchResults) {
      return <TopBands />;
    }

    return (
      <div>
        <h2 className="ui header">Top Bands</h2>
        <div className="ui stackable four column grid">


        {this.props.searchResults.map(result => {
          return (
            <div key="" className="row">
              <div className="column"><BandCard imagePath={this.props.searchResults.profileImage} bandName={this.props.searchResults.name} numStans={this.props.searchResults.stanCount} /></div>
            </div>
          );
        })}

        </div>
      </div>
    );


  }

  render() {
    return (
      <div className="center">
        <Search />
        {this.renderBands()}
      </div>
    );
  }

}

const mapStateToProps = state => {
  return {
    searchValue: state.bandSearch.value,
    searchResults: state.bandSearch.results
  };
};

export default connect(
  mapStateToProps
)(Home);
