import React from 'react';

import users from '../../apis/users';

class Comment extends React.Component {

  constructor(props) {
    super(props);
    this.state = { imageUrl: '' };
  }

  fetchImageUrl = async () => {
    const response = await users.get(`?userId=${this.props.comment.userId}`);
    this.setState({ imageUrl: response.data[0].imageUrl });
  }

  componentDidMount() {
    console.log("load comment image");
    this.fetchImageUrl();
  }

  render() {

    const comment = this.props.comment;

    return (
      <div className="ui comments">
        <div className="comment">
          <a className="avatar">
            <img src={this.state.imageUrl} />
          </a>
          <div className="content">
            <a className="author">{comment.userId}</a>
            <div className="metadata">
              <div className="date">{comment.timePosted}</div>
              <div className="rating">
                <i className="star icon"></i>
                {comment.upvotes} Faves
              </div>
            </div>
            <div className="text">
              {comment.body}
            </div>
          </div>
        </div>
      </div>
    );
  }

}

export default Comment;
