import React from 'react';
import { connect } from 'react-redux';

class Band extends React.Component {

  render() {
    return (
      <div style={{ margin: '15px' }}>

        <div className="ui segment">
          <div className="ui items">
            <div className="item">
              <div className="ui medium rounded image">
                <img src={this.props.currentBand.profileImage} alt={this.props.currentBand.name} />
              </div>
              <div className="top aligned content">
                <div className="header"><h1>{this.props.currentBand.name}</h1></div>
                <div className="description">{this.props.currentBand.stanCount} stans</div>
                <div className="extra">
                  <div className="ui secondary focusable button"><h3>Stan</h3></div>
                </div>
              </div>
            </div>
          </div>
        </div>


      </div>
    );
  }

}

const mapStateToProps = state => {
  return {
    currentBand: state.bandSearch.selected
  };
};

export default connect(
  mapStateToProps
)(Band);
